int xmlSave() {
    QTime time;
    time.start();

    QFile file("test.xml");
    if (!file.open(QFile::WriteOnly))
        return 0;

    QString fileStr;
    QXmlStreamWriter elem(&fileStr);
    elem.writeStartDocument();
    elem.writeStartElement("Page");
    elem.writeStartElement("clientID");
    elem.writeAttribute("V", "11111.111");
    elem.writeEndElement();
    elem.writeStartElement("type");
    elem.writeAttribute("V", "1");
    elem.writeEndElement();
    elem.writeStartElement("colors");
    for (int i = 0; i < 100000; i++) {
        elem.writeStartElement("color");
        elem.writeAttribute("V", "#00bbcc");
        elem.writeEndElement();
    }
    elem.writeEndElement();
    elem.writeTextElement("str", "");
    elem.writeEndElement();
    elem.writeEndDocument();

    file.write(fileStr.toUtf8());
    int costTime = time.elapsed();
//    qDebug() << "--- xml save time: " << costTime << "ms, fileSize: " << fileStr.size();

    file.close();
    return costTime;
}

int xmlParse() {
    QTime time;
    time.start();

    QFile file("test.xml");
    if (!file.open(QFile::ReadOnly))
        return 0;

    QXmlStreamReader reader;
    reader.setDevice(&file);
    BasicInfo info;
    reader.readNext();
    while (!reader.atEnd()) {
//        qDebug() << "----reader name: " << reader.isStartElement() << reader.isEndElement() << reader.name();
        if (reader.isStartElement()) {
            QString tagName = reader.name().toString();
            if (tagName == "clientID") {
                info.m_clientID = reader.attributes().value("V").toDouble();
//                qDebug() << "----id " << QString::number(info.m_clientID, 'g', 10);
            } else if (tagName == "type") {
                info.m_type = reader.attributes().value("V").toUInt();
            } else if (tagName == "colors") {
                reader.readNext();
                while (!reader.atEnd()) {
                    tagName = reader.name().toString();
                    if (tagName == "color") {
                        QString clrStr = reader.attributes().value("V").toString();
                        QColor clr(clrStr);
                        Colors clrs;
                        clrs.m_color[0] = clr.red();
                        clrs.m_color[1] = clr.green();
                        clrs.m_color[2] = clr.blue();
                        clrs.m_color[3] = clr.alpha();
                        info.m_clrs.push_back(clrs);
                    }
                    if (reader.isEndElement()) {
                        if (tagName == "colors")
                            break;
                    }
                    reader.readNext();
                }
            } else if (tagName == "str") {
                reader.readNext();
                if (reader.isCharacters()) {
                    info.m_name = reader.text().toString();
                }
            }
        }

        reader.readNext();
    }

    if (reader.hasError())
        qDebug() << "------XmlStreamReader error == " << reader.errorString();

    int costTime = time.elapsed();
//    qDebug() << "--- xml parse time: " << costTime << "ms，fileSize: " << file.size();
//    qDebug() << "--- client ID: " << QString::number(info.m_clientID, 'g', 10);

    file.close();
    return costTime;
}

int main(int argc, char *argv[]) {
    QApplication a(argc, argv);
//    qDebug() << "十万级数组数据+长字符串，XML与Kiwi读写性能对比：";
    qreal allReadKiwi = 0, allReadXml = 0;
    qreal allWriteKiwi = 0, allWriteXml = 0;
    int count = 5;
    for (int i = 0; i < count; i++) {
        allWriteXml += xmlSave();
        allWriteKiwi += serialize();
        allReadXml += xmlParse();
        allReadKiwi += deserialize();
    }
    qDebug() << "平均读写时长: ";
    qDebug() << "Kiwi   读:" << allReadKiwi / count << "ms, 写:" << allWriteKiwi / count;
    qDebug() << "XML   读:" << allReadXml / count << "ms, 写:" << allWriteXml / count;

    return 0;
}
